import React, { Component } from 'react';
import RoundAbout from './RoundAbout';
import { Button, Form, Label, FormGroup, Input, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import LoginModal from '../components/LoginModal/Modal';

export default class Login extends Component {
    constructor (props) {
        super(props);
        this.state = {
          modal: false,
          nestedModal: false,
          closeAll: false,
          username: "",
          password: "",
          authenticated: false
        };

        this.toggle = this.toggle.bind(this);
        this.toggleNested = this.toggleNested.bind(this);
        this.toggleAll = this.toggleAll.bind(this);
      }

      toggle () {
        this.setState(prevState => ({
          modal: !prevState.modal
        }));
      }

      toggleNested () {
        this.setState({
          nestedModal: !this.state.nestedModal,
          closeAll: false
        });
      }

      toggleAll () {
        this.setState({
          nestedModal: !this.state.nestedModal,
          closeAll: true
        });
      }

      handleSubmit = (event) => {
        event.preventDefault();
        const registerData = {
            email: this.state.email,
            // username: this.state.username,
            password: this.state.password
        }
        Axios.get('/api/members', registerData)
            .then(results => {
                console.log('Attempted Login:', results);
            })
            .catch(error => {
                console.log(error.response);
            })
        }
    render() {
        console.log('this props', this.props);
        return (
         <div>
             <Modal isOpen={this.props.showModal} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>MyVacTRACK Login</ModalHeader>
          <ModalBody>
            <Form>
              <FormGroup>
                <Label for="Email">e-mail</Label>
                <Input type="email" name="email" id="eEmail" placeholder="e-mail address" />
              </FormGroup>
              <FormGroup>
                <Label for="password">Password</Label>
                <Input type="password" name="password" id="password" placeholder="password" />
              </FormGroup>
            </Form>
            <br />
            <Button type="submit" className="btn btn-primary">Submit</Button>
            </ModalBody>
            <ModalFooter>
Not Yet Registered?
<Button color="success" onClick={this.toggleNested}> Get Started </Button> {' '}
{console.log('Getting Started Button', this.toggleNested)}
<Button color="secondary" onClick={this.toggle}>Cancel</Button>
</ModalFooter>
            </Modal>
            <RoundAbout />
        </div>
        )
    }
}
