var mongoose = require("mongoose");

var Schema = mongoose.Schema; // change all lines below to match our database

var UserSchema = new Schema({
  email: {
    type: String,
    required: true
  },
  password: {
    type: String,
    required: true
  },
  date: {
    type: Date,
    default: Date.now
  }
});
var Users = mongoose.model("Users", UserSchema);
module.exports = Users;
